import React from 'react';
import { Modal, TextField, TextareaAutosize, Typography } from '@material-ui/core';
import { useSelector } from 'react-redux';

import { modalState$ } from '../../redux/selectors';
import useStyles from './styles';

export default function CreatePostModels() {
    //Calling API to get data
    const { isShow } = useSelector(modalState$);
    const classes = useStyles();

    const body = (
        <div className ={classes.popup} id ="simple-modal-title">
            <Typography display='flex' variant='h4' align='center' className={classes.typography}>Create New Post</Typography>
            <form noValidate autoComplete='off' className={classes.form}>
                <TextField
                    className={classes.title}
                    required
                    label ='Title'
                    value=""
                />
                <TextareaAutosize
                    className={classes.textarea}
                    maxRows={15}
                    minRows={10}
                    label ='Content...'
                />
            </form>
        </div>
    );
    console.log({isShow});
    return (
        <div>
            <Modal open ={ isShow }>{body}</Modal>
        </div>
    )
}
